mapper.insert:

    	SELECT CONCAT(UPPER(LEFT(MD5(UUID()), 5)),'-',UPPER(LEFT(MD5(UUID()), 5)))

	INSERT INTO KT.GV_USER
	(
		user_id
		,mail
		,pw
		,nm
		,salt
	)
	VALUES
	(
		#{userId}
		,#{mail}
		,#{pw}
		,HEX(AES_ENCRYPT(#{nm},SHA2('a1s2d3f4g5h6j7k8l9!',512)))
		,#{salt}
	)

mapper.select:
SELECT user_id , (CAST(AES_DECRYPT(UNHEX(nm),SHA2('a1s2d3f4g5h6j7k8l9!',512)) AS CHAR)) AS nm , (CAST(AES_DECRYPT(UNHEX(mail),SHA2('a1s2d3f4g5h6j7k8l9!',512)) AS CHAR)) AS mail , salt , pw , wrong_pw FROM KT.GV_USER WHERE 1=1
AND active = 1
AND user_id = #{userId}
AND pw = #{pw}
AND mail = #{userMail}
AND last_repw_date > DATE_SUB(NOW(), INTERVAL 1 DAY)

mapper.update:
UPDATE KT.GV_USER
SET
	last_repw_date = now()

	 , active			= #{active}


	 , intro	= #{intro}


	 , wrong_pw	= #{wrongPw}


	 , pw				= #{newPwd}
	 , last_repw_date = current_timestamp()

WHERE  1=1

	AND user_id = #{userId}


	AND mail = #{userMail}

mapper.insert:
INSERT INTO KT.GV_USER_TOKEN
(
	user_id
	, token
	, refresh_token
	, pub_date
)
VALUES
(
	#{userId}
	, #{token}
	, #{refreshToken}
	, NOW()
)
ON DUPLICATE KEY UPDATE 
	token	=	#{token} 
	, refresh_token	= #{refreshToken}
	, pub_date = NOW()

mapper.update:
UPDATE KT.GV_USER_TOKEN
SET
	pub_date = now()

	 , token			= #{token}


	 , refresh_token	= #{refreshToken}

WHERE user_id = #{userId}

