	INSERT INTO KT.GV_GUEST
	(
		guest_nick_nm
		, ip_add
		, state
		, state_code

		, guest_id


		, guest_url

	)
	VALUES
	(
		#{guestNickNm}
		, #{ipAdd}
		, '온라인'
		, 'CODE001'

		, #{guestId}


		, #{guestUrl}

	)

mapper.select:
SELECT guest_id , guest_nick_nm , ip_add , IFNULL(guest_url, '') AS guest_url , IFNULL(img_log, '') AS img_log , state_code FROM KT.GV_GUEST WHERE 1=1
AND guest_id = #{guestId}
AND guest_nick_nm = #{guestNickNm}

mapper.update:
UPDATE KT.GV_GUEST
SET
	guest_id = #{guestId}

	, guest_nick_nm = #{guestNickNm}


	, guest_url = #{guestUrl}


	, img_log = #{imgLog}


	, state_code = #{stateCode}

WHERE guest_id = #{guestId}

